<?php //speed up admin pagefunction disable_dashboard_widgets() {       remove_meta_box('dashboard_recent_comments', 'dashboard', 'normal');    remove_meta_box('dashboard_recent_drafts', 'dashboard', 'normal');    remove_meta_box('dashboard_primary', 'dashboard', 'core');    remove_meta_box('dashboard_secondary', 'dashboard', 'core');    remove_meta_box('dashboard_right_now', 'dashboard', 'core');    remove_meta_box('dashboard_incoming_links', 'dashboard', 'core');    remove_meta_box('dashboard_plugins', 'dashboard', 'core');    remove_meta_box('dashboard_quick_press', 'dashboard', 'core');}  add_action('admin_menu', 'disable_dashboard_widgets');function cwp_remove_dashboard_widgets() {    global $wp_meta_boxes;    unset($wp_meta_boxes['dashboard']['side']['core']['dashboard_quick_press']);    unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_incoming_links']);    unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_right_now']);    unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_plugins']);    unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_recent_drafts']);    unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_recent_comments']);    unset($wp_meta_boxes['dashboard']['side']['core']['dashboard_primary']);    unset($wp_meta_boxes['dashboard']['side']['core']['dashboard_secondary']);}add_action('wp_dashboard_setup', 'cwp_remove_dashboard_widgets',11 );?>